    {{-- les checkbox du tableau pour avoir activé selectionner tous --}}
    <script>
        $(document).ready(function() {


            const compare = (ids, asc) => (row1, row2) => {
                const tdValue = (row, ids) => row.children[ids].textContent;
                const tri = (v1, v2) => v1 !== '' && v2 !== '' && !isNaN(v1) && !isNaN(v2) ? v1 - v2 : v1
                    .toString().localeCompare(v2);
                return tri(tdValue(asc ? row1 : row2, ids), tdValue(asc ? row2 : row1, ids));
            };

            const tbody = document.querySelector('tbody');
            const thx = document.querySelectorAll('th');
            const trxb = tbody.querySelectorAll('tr');
            thx.forEach(th => th.addEventListener('click', () => {
                let classe = Array.from(trxb).sort(compare(Array.from(thx).indexOf(th), this.asc = !this
                    .asc));
                classe.forEach(tr => tbody.appendChild(tr));
            }));

            // Activate tooltip
            $('[data-toggle="tooltip"]').tooltip();

            // Select/Deselect checkboxes
            var checkbox = $('table tbody input[type="checkbox"]');
            $("#selectAll").click(function() {
                if (this.checked) {
                    checkbox.each(function() {
                        this.checked = true;
                    });
                } else {
                    checkbox.each(function() {
                        this.checked = false;
                    });
                }
            });
            checkbox.click(function() {
                if (!this.checked) {
                    $("#selectAll").prop("checked", false);
                }
            });
        });

        function Generer() {

            var password = Math.random().toString(36).substring(2, 8);

            $("#password").val(password);
        }

        setTimeout(() => {
            $(".alert").fadeOut()
        }, 2000);

        function addUsers() {
            // alert()
            $('.addUsers').removeClass("d-none").addClass("")
            // $('.addUsers').AddClass('d-block')
        }

        function SupprimerChecked() {
            // e.preventDefault()
            if (confirm('voulez-vous supprimer ces employes')) {
                if ($('#selectAll').is(':checked')) {
                    window.location = `deleteSelectedEmploye/all`
                } else {
                    var liste = [];
                    $('.sellectedEmployes').each((index, element) => {
                        if (element.checked) {
                            liste.push(element.value)

                        }

                        var val = liste.join('-')
                        window.location = `deleteSelectedEmploye/${val}`
                    });
                }

            }
        }
    </script>
